package com.startjava.lesson_2_3_4.game;import java.util.*;public class GuessNumber {	private Random rand = new Random();	private Scanner scan = new Scanner(System.in);	private Player player1;    private Player player2;	private int computerRandomNumber;    private int currentTry;	private static final int MAX_TRIES = 10;	private static final int RAND_MAX = 100;	public GuessNumber(Player player1, Player player2) {		this.player1 = player1;		this.player2 = player2;	}	public void startGame() {        generateRandomNum();        currentTry = 0;        do {            currentTry++;            inputPlayerNum(player1);            if (checkPlayerNum(player1)) {                break;            }            inputPlayerNum(player2);            if (checkPlayerNum(player2)) {            	break;			}        } while (currentTry < MAX_TRIES);        if (currentTry >= MAX_TRIES){            System.out.println("You have no lives left. Insert coin to continue.");            printPlayersNums();            resetPlayers();        }	}	private void generateRandomNum() {		System.out.println("Computer guessed the number from 1 to " + (RAND_MAX) + ". What number is it?");		computerRandomNumber = rand.nextInt(RAND_MAX)+1;		System.out.println("Note: you have only " + MAX_TRIES +" tries.");	}	private void inputPlayerNum(Player player) {        System.out.println(player.getName() + ": enter you answer:");        player.setNumber(scan.nextInt());    }    private void printPlayersNums() {	    System.out.print(player1.getName() + " numbers: ");	    player1.numbersToString();        System.out.print(player2.getName() + " numbers: ");        player2.numbersToString();    }    private void resetPlayers() {        player1.reset();        player2.reset();    }	private boolean checkPlayerNum(Player player) {		if (player.getNumber() == computerRandomNumber) {		    System.out.println("Player " + player.getName() + " wins with " + (10 - currentTry) + " lives left! \n");		    printPlayersNums();		    resetPlayers();		    return true;		} else if (player.getNumber() < computerRandomNumber) {		    System.out.println(player.getName() + "'s number is less. \n");		    return false;		} else {		    System.out.println(player.getName() + "'s number is greater. \n");		    return false;		}	}}