package com.startjava.lesson_2_3_4.game;import java.util.Arrays;public class Player {	private String name;	private int number = -1;	private static final int MAX_TRIES = 10;	private int[] numbers = new int[MAX_TRIES];	private int currentNumberIndex = -1;	public Player(String name) {		this.name = name;	}	private void updateNumberIndex() {        currentNumberIndex++;    }	public String getName() {		return name;	}    public void setNumber(int number) {        updateNumberIndex();	    numbers[currentNumberIndex] = number;        this.number = number;    }	public int getNumber() {        return number;    }    public void reset() {        Arrays.fill(numbers, 0);        currentNumberIndex = -1;        number = 0;    }    public void numbersToString() {        for (int i = 0; i <= currentNumberIndex; i++) {            System.out.print(numbers[i] + " ");        }        System.out.println();    }}