package com.startjava.lesson_2_3_4.game;import java.util.Arrays;public class Player {	private String name;	private int number = -1;	private static final int MAX_TRIES = 10;	private int[] numbers = new int[MAX_TRIES];	private int currentNumberIndex = -1;	private boolean isWinner;	public Player(String name) {		this.name = name;	}	private void updateNumberIndex() {        currentNumberIndex++;    }	public String getName() {		return name;	}    public void setNumber(int number) {        updateNumberIndex();	    numbers[currentNumberIndex] = number;        this.number = number;    }	public int getNumber() {        return number;    }	public int getNumberWithIndex(int index) {		return numbers[index];	}    public boolean isWinner() {        return isWinner;    }    public void setWinner(boolean winner) {        isWinner = winner;    }    public void reset() {        Arrays.fill(numbers, 0);        currentNumberIndex = -1;        isWinner = false;        number = 0;    }}